.box {
  width: 200px;
  height: 100px;
  padding: 5px;
  border: 1px solid limegreen;
  background-color: hotpink;
}

.title {
  font-size: 20px;
  margin: 10px 0 20px;
}
.list {
  background: grey;
  padding-left: 20px;
}
.item {
  margin: 10px 0;
  list-style-type: square;
}

